import React from 'react';
import { useNavigate } from 'react-router-dom';

const products = [
    {id: 1, name: 'Washing Machine', description: '6kg capacity', price: '₺2000', image: 'https://via.placeholder.com/150'},
    {id: 2, name: 'Refrigerator', description: 'No frost technology', price: '₺4500', image: 'https://via.placeholder.com/150'},
    { id: 3, name: 'Microwave Oven', description: '800W with grill', price: '₺1500', image: 'https://via.placeholder.com/150' },
];


const MainPage = () => {
    const navigate = useNavigate();

    const handleLoginClick = () => {
        navigate('/login');
    };



    return (
        <div style={{ padding: '20px' }}>
        <h1>BeyazEvim - Your White Goods Store</h1>
        <button onClick={handleLoginClick} style={{ marginBottom: '20px', padding: '10px', backgroundColor: '#007bff', color: 'white', borderRadius: '5px' }}>
            Login
        </button>
        <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
            {products.map((product) => (
            <div key={product.id} style={{ border: '1px solid #ddd', padding: '10px', width: '200px', borderRadius: '10px' }}>
                <img src={product.image} alt={product.name} style={{ width: '100%', borderRadius: '10px' }} />
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <p style={{ fontWeight: 'bold' }}>{product.price}</p>
            </div>
            ))}
        </div>
        </div>

    );

};

export default MainPage;
