// src/pages/MainPage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';

// Dummy product data
const products = [
  {
    id: 1,
    name: 'Washing Machine',
    description: '6kg capacity top load washing machine',
    price: '₺2000',
    image: 'https://via.placeholder.com/150',
  },
  {
    id: 2,
    name: 'Refrigerator',
    description: 'Double door with no frost technology',
    price: '₺4500',
    image: 'https://via.placeholder.com/150',
  },
  {
    id: 3,
    name: 'Microwave Oven',
    description: '800W with grill function',
    price: '₺1500',
    image: 'https://via.placeholder.com/150',
  },
];

const MainPage = () => {
  const navigate = useNavigate(); // Hook to navigate between pages

  // Login button handler
  const handleLoginClick = () => {
    navigate('/login'); // Navigate to the login page
  };

  // Return statement to render the UI
  return (
    <div style={{ padding: '20px' }}>
      <h1>BeyazEvim - Your White Goods Store</h1>

      <button
        onClick={handleLoginClick}
        style={{
          marginBottom: '20px',
          padding: '10px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Login
      </button>

      <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
        {products.map((product) => (
          <div
            key={product.id}
            style={{
              border: '1px solid #ddd',
              borderRadius: '10px',
              padding: '10px',
              width: '200px',
            }}
          >
            <img
              src={product.image}
              alt={product.name}
              style={{ width: '100%', borderRadius: '10px' }}
            />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p style={{ fontWeight: 'bold' }}>{product.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MainPage; // Exporting the MainPage component
